import { CartProvider, useCart } from "react-use-cart";
import Navbar from "../Navbar";
import Header from "../Header";
import { Col, Container, Row } from "reactstrap";

function Page() {
  const { addItem } = useCart();

  const products = [
    {
      "albumId": 1,
      "id": 1,
      "price":100,
      "title": "accusamus beatae",
      "url": "https://via.placeholder.com/600/92c952",
      "thumbnailUrl": "https://via.placeholder.com/150/92c952"
    },
    {
      "albumId": 1,
      "id": 2,
      "price":120,
      "title": "reprehenderit ",
      "url": "https://via.placeholder.com/600/771796",
      "thumbnailUrl": "https://via.placeholder.com/150/771796"
    },
    {
      "albumId": 1,
      "id": 3,
      "price":150,
      "title": "officia porro ",
      "url": "https://via.placeholder.com/600/24f355",
      "thumbnailUrl": "https://via.placeholder.com/150/24f355"
    },
    {
      "albumId": 1,
      "id": 4,
      "price":150,
      "title": "culpa odio ",
      "url": "https://via.placeholder.com/600/d32776",
      "thumbnailUrl": "https://via.placeholder.com/150/d32776"
    },
    {
      "albumId": 1,
      "id": 5,
      "price":250,
      "title": "natus nisi",
      "url": "https://via.placeholder.com/600/f66b97",
      "thumbnailUrl": "https://via.placeholder.com/150/f66b97"
    },
    {
      "albumId": 1,
      "id": 6,
      "price":290,
      "title": "accusamus ea ",
      "url": "https://via.placeholder.com/600/56a8c2",
      "thumbnailUrl": "https://via.placeholder.com/150/56a8c2"
    },
    {
      "albumId": 1,
      "id": 7,
      "price":300,
      "title": "officia delectus ",
      "url": "https://via.placeholder.com/600/b0f7cc",
      "thumbnailUrl": "https://via.placeholder.com/150/b0f7cc"
    },
    {
      "albumId": 1,
      "id": 8,
      "price":350,
      "title": "aut porro officiis ",
      "url": "https://via.placeholder.com/600/54176f",
      "thumbnailUrl": "https://via.placeholder.com/150/54176f"
    },
    {
      "albumId": 1,
      "id": 9,
      "price":390,
      "title": "qui eius ",
      "url": "https://via.placeholder.com/600/51aa97",
      "thumbnailUrl": "https://via.placeholder.com/150/51aa97"
    }
  ]
  ;
  
  
  return (
    <div>
      <Container>
        <Row>
          <Col md="3">
          {products.map((p) => (
        <div key={p.id}>
          <img src={p.url} className="img-fluid"></img>
          <h4>{p.title}</h4>
          <button onClick={() => addItem(p)}>Add to cart</button>
        </div>
        ))}
          </Col>
        </Row>
      </Container>

      
    </div>
  );
}

function Cart() {
  const { isEmpty, totalUniqueItems, totalItems, items, updateItemQuantity, removeItem, } = useCart();
  if (isEmpty) return <h1 className="text-center text-danger">Your cart is empty</h1>;

  return (
    <CartProvider>
      <h1 className="text-center">Cart({totalUniqueItems})</h1>
      <Container>
        <Row>
          <Col md="12" className="m-auto border-none ">
            <table className="table table-dark">
              <thead>
                <tr>
                  <td>Name</td>
                  <td>Image</td>
                  <td>Quantity</td>
                  <td>Price</td>
                </tr>
              </thead>
              <tbody>
                {items.map((item) => (
                  <tr>
                    <td>{item.title}</td>
                    <td key={item.url}></td>
                    <td>{item.price}</td>
                    <td><button
                      onClick={() => updateItemQuantity(item.id, (item.quantity ?? 0) - 1)}
                    >
                      -
                    </button>
                      <button
                        onClick={() => updateItemQuantity(item.id, (item.quantity ?? 0) + 1)}
                      >
                        +
                      </button>
                      <button onClick={() => removeItem(item.id)}>&times;</button></td>

                  </tr>
                ))}
              </tbody>
            </table>
          </Col>
        </Row>
      </Container>
    </CartProvider>
  );
}

function Allcart() {
  return (
    <CartProvider>
      <Navbar />
      <Header />
      <br></br>
      <br></br>
      <Page />
      <Cart />
    </CartProvider>
  );
}
export default Allcart
